<style>
.emoji-drawer {
    display: grid;

    grid-template-columns: repeat(8, 1fr);


    transition: opacity 0.2s;

    margin-left: auto;
  margin-right: auto;
}

.hidden {
  opacity: 0;
  display: none;
}

.emoji {
    text-align: center;
    font-size: 24px;
    padding: 8px;
}

.emoji:hover {
    cursor: pointer;
}

.emojibtn {
  margin-right: 5px;
  height: 30px;
  height: calc(1.4em + 1.4rem + 2px);
  border-radius: 10px;
  background: rgb(22,22,22);
  color: #c1bcbc;
  padding: 10px;
  font-size: 16px;
  margin-top: 3px;
  height: 43px;
}



.loader {
  font-size: 20px;
  position: relative;
  width: 4em;
  height: 1em;
  margin-right: auto;
  margin-left: auto;
}

.dot {
  width: 1em;
  height: 1em;
  border-radius: 0.5em;
  background: #c1c1c1;
  position: absolute;
  animation-duration: 0.5s;
  animation-timing-function: ease;
  animation-iteration-count: infinite;
}

.dot1, .dot2 {
  left: 0;
}

.dot3 { left: 1.5em; }

.dot4 { left: 3em; }

@keyframes reveal {
  from { transform: scale(0.001); }
  to { transform: scale(1); }
}

@keyframes slide {
  to { transform: translateX(1.5em) }
}


.dot1 {
  animation-name: reveal;
}

.dot2, .dot3 {
  animation-name: slide;
}

.dot4 {
  animation-name: reveal;
  animation-direction: reverse; /* thx @HugoGiraudel */
}

.ring-container {
    position: relative;
}

.circle1 {
    width: 15px;
    height: 15px;
    background-color: #62bd19;
    border-radius: 50%;
    position: absolute;
    left: 10px;
  top: 15px;
}

.ringring1 {
    border: 3px solid #62bd19;
    -webkit-border-radius: 30px;
    height: 25px;
    width: 25px;
    position: absolute;
    left: 5px;
  top: 10px;
    -webkit-animation: pulsate 1s ease-out;
    -webkit-animation-iteration-count: infinite; 
    opacity: 0.0
}

.circle2 {
    width: 15px;
    height: 15px;
    background-color: #ffd05f;
    border-radius: 50%;
    position: absolute;
    left: 10px;
  top: 15px;
}

.ringring2 {
    border: 3px solid #ffd05f;
    -webkit-border-radius: 30px;
    height: 25px;
    width: 25px;
    position: absolute;
    left: 5px;
  top: 10px;
    -webkit-animation: pulsate 1s ease-out;
    -webkit-animation-iteration-count: infinite; 
    opacity: 0.0
}

@-webkit-keyframes pulsate {
    0% {-webkit-transform: scale(0.1, 0.1); opacity: 0.0;}
    50% {opacity: 1.0;}
    100% {-webkit-transform: scale(1.2, 1.2); opacity: 0.0;}
}

  .name{
    width:120px;
    text-overflow: ellipsis;
    cursor: pointer;
    overflow:hidden;
    white-space: nowrap;
}
.name:hover{
    overflow: visible; 
    white-space: normal;
    height:auto;  /* just added this line */
}

  .btn-group-sm > .btn, .btn-sm {
      border-radius: 1.0rem;
  }
  .node-stats {
      color: #1774FF  }

  .profiles1 { font-weight: 500 !important; }

  .form-control::placeholder {
    color: #d7d7d7 !important;
  }
</style>





